import React from 'react';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import timeGridPlugin from '@fullcalendar/timegrid';
import interactionPlugin from '@fullcalendar/interaction';
import {useFetch, getNext4MonthsEvents} from '../hooks/useFetch';
import {Spinner } from 'react-bootstrap';

const Home = () => {

  const [data, isLoading] = useFetch();
  if(data) {
    return (
    <Spinner animation="border" role="status">      
    </Spinner>
    )
  }

  return (  
    <div className='container'>
      {isLoading.length > 0 && isLoading.map((list) => (

       <FullCalendar     
          plugins={[dayGridPlugin, timeGridPlugin, interactionPlugin]}
          initialView="timeGridDay"
          contentHeight={"auto"}
          validRange={{
          start: new Date(),
          end: getNext4MonthsEvents()
          }}
          //TODO need to add a unique key
          key={list[0].key }
          //dayHeaders={false}
          dayHeaderContent={"list[0].guild"}
          dayHeaderClassNames={"guild-name"}
          slotMinTime={"08:00:00"}
          slotMaxTime={"24:00:00"}

          events={list}
          eventColor="#CF9FFF"
          nowIndicator
          dateClick={(e) => console.log(e.dateStr)}
          eventClick={(e) => console.log(e.event.id)}
          timeZone="Europe/Helsinki"
        />

         ))}
      </div>      
    );
};

export default Home;
