import React from 'react';
import './App.css';

import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import timeGridPlugin from '@fullcalendar/timegrid';
import interactionPlugin from '@fullcalendar/interaction';

function App() {
  const [data, setData] = React.useState(null);

  React.useEffect(() => {
    fetch("/events", {
      method: 'POST',
      body: JSON.stringify({
        startDateTimeFrame : '2022-10-04T00:00:00Z',
        endDateTimeFrame : '2022-10-08T00:00:00Z'
      }),
      headers: {'Content-type': 'application/json; charset=UTF-8'},
    })
      .then((res) => res.json())
      .then((data) => {
        console.log("data ", JSON.stringify(data.response));
        setData(JSON.stringify(data.response))
      });
  }, []);

  /*return (
    <div className="App">
      <header className="App-header">
        <p>{!data ? "Loading..." : data}</p>
      </header>
    </div>
  );*/

  return (
    <div className="App">
      <div className='container'>
      <FullCalendar
        plugins={[dayGridPlugin, timeGridPlugin, interactionPlugin]}
        initialView="timeGridDay"
        //dayHeaders={false}
        dayHeaderContent={"Guild 1"}
        dayHeaderClassNames={"guild-name"}
        slotMinTime={"08:00:00"}
        slotMaxTime={"24:00:00"}

        /*headerToolbar={{
          center: 'dayGridMonth,timeGridWeek,timeGridDay new',
        }}*/
        /*customButtons={{
          new: {
            text: 'new',
            click: () => console.log('new event'),
          },
        }}*/
        events={data}
        eventColor="red"
        nowIndicator
        dateClick={(e) => console.log(e.dateStr)}
        eventClick={(e) => console.log(e.event.id)}
        contentHeight={'auto'}
      />
      <FullCalendar
        plugins={[dayGridPlugin, timeGridPlugin, interactionPlugin]}
        initialView="timeGridDay"
        /*headerToolbar={{
          center: 'dayGridMonth,timeGridWeek,timeGridDay new',
        }}*/
        /*customButtons={{
          new: {
            text: 'new',
            click: () => console.log('new event'),
          },
        }}*/
        events={data}
        eventColor="red"
        nowIndicator
        dateClick={(e) => console.log(e.dateStr)}
        eventClick={(e) => console.log(e.event.id)}
        contentHeight={'auto'}
      />
      </div>
    </div>
  );

}

export default App;
