import React from 'react';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import timeGridPlugin from '@fullcalendar/timegrid';
import interactionPlugin from '@fullcalendar/interaction';

const Home = () => {

    const [guildsOfGuilds, setData] = React.useState(null);
    const guilds = ['TIK', 'AK', 'Athene', 'AS', 'IK', 'Inkubio', 'KIK', 'MK', 'PJK', 'PT', 'TF', 'VK', 'Prodeko', 'FK' ];


    const guildCalendars = guildsOfGuilds.map((list)=>
        <FullCalendar
        plugins={[dayGridPlugin, timeGridPlugin, interactionPlugin]}
        initialView="timeGridDay"
        contentHeight={"auto"}
        validRange={{
        start: new Date(),
        end: getNext4MonthsEvents()
        }}
        //dayHeaders={false}
        dayHeaderContent={list[0].guild}
        dayHeaderClassNames={"guild-name"}
        slotMinTime={"08:00:00"}
        slotMaxTime={"24:00:00"}

        /*headerToolbar={{
        center: 'dayGridMonth,timeGridWeek,timeGridDay new',
        }}*/
        /*customButtons={{
        new: {
            text: 'new',
            click: () => console.log('new event'),
        },
        }}*/
        events={list}
        eventColor="#CF9FFF"
        nowIndicator
        dateClick={(e) => console.log(e.dateStr)}
        eventClick={(e) => console.log(e.event.id)}
        timeZone="Europe/Helsinki"
    />
    );

    React.useEffect(() => {
      fetch("/events", {
        method: 'POST',
        body: JSON.stringify({
          guildNames: "Plus",
          startDateTimeFrame : new Date(),
          endDateTimeFrame : getNext4MonthsEvents()
        }),
        headers: {'Content-type': 'application/json; charset=UTF-8'},
      })
        .then((res) => res.json())
        .then((data) => {
            console.log("data", data);
          var dataString = JSON.stringify(data.response);
          var result = JSON.parse(dataString);
          console.log("result ", result);
          setData(result)
        });
    }, []);

    function getNext4MonthsEvents(){
      var date = new Date();
      date.setMonth(date.getMonth() + 4);
      return date.toISOString();
    }


  return (
    <div className='container'>
        {guildCalendars}
    </div>

    );
};

export default Home;
