import React from 'react';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import timeGridPlugin from '@fullcalendar/timegrid';
import interactionPlugin from '@fullcalendar/interaction';
import {getNext4MonthsEvents} from '../hooks/useFetch';

const Home = () => {

  const [error, setError] = React.useState(false);
  const [response, setResponse] = React.useState([]);
  async function fetchData() {
    try {
      const guilds = ['TIK', 'AK', 'Athene', 'AS', 'IK', 'Inkubio', 'KIK', 'MK', 'PJK', 'PT', 'TF', 'VK'];
      await Promise.all([
       (
         await fetch("/events", {
          method: 'POST',
          body: JSON.stringify({
            guildNames: guilds,
            startDateTimeFrame : new Date(),
            endDateTimeFrame : getNext4MonthsEvents()
          }),
          headers: {'Content-type': 'application/json; charset=UTF-8'},
        }).then((res) => res.json())
        .then((data) => {
          var dataString = JSON.stringify(data.response);
          //console.log("dataString ", dataString);
          var result = JSON.parse(dataString);
          //console.log("result ", result);

          setResponse(result);
        }))
     ]);


   } catch (error) {
     console.log(error);
     setError(true);
   }
 }

 React.useEffect(() => {
   fetchData();
 }, []);

 React.useEffect(() => {
   console.log(response);
 }, [response]);


 if(error) {
  return (
  <h1> Oops.. something went wrong!     
  </h1>
  )
}

return (  
  <div className='container'>
    {response.length > 0 && response.map((list) => (

     <FullCalendar     
        plugins={[dayGridPlugin, timeGridPlugin, interactionPlugin]}
        initialView="timeGridDay"
        contentHeight={"auto"}
        validRange={{
        start: new Date(),
        end: getNext4MonthsEvents()
        }}
        //TODO need to add a unique key

        //dayHeaders={false}
        dayHeaderContent={"list[0].guild"}
        dayHeaderClassNames={"guild-name"}
        slotMinTime={"08:00:00"}
        slotMaxTime={"24:00:00"}

        events={list}
        eventColor="#CF9FFF"
        nowIndicator
        dateClick={(e) => console.log(e.dateStr)}
        eventClick={(e) => console.log(e.event.id)}
        timeZone="Europe/Helsinki"
      />

       ))}
    </div>      
  );



};

export default Home;
