import React from "react";

export default function useFetch(url) {

  //   Must use useEffect in non-page component

  const [data, isLoading, setData] = React.useState({ data: [], isLoading: true });
  const guilds = ['TIK', 'AK', 'Athene', 'AS', 'IK', 'Inkubio', 'KIK', 'MK', 'PJK', 'PT', 'TF', 'VK', 'Prodeko', 'FK' ];
  React.useEffect(() => {
    fetch("/events", {
      method: 'POST',
      body: JSON.stringify({
        guildNames: guilds,
        startDateTimeFrame : new Date(),
        endDateTimeFrame : getNext4MonthsEvents()
      }),
      headers: {'Content-type': 'application/json; charset=UTF-8'},
    })
      .then((res) => res.json())
      .then((data) => {
        var dataString = JSON.stringify(data.response);
        var result = JSON.parse(dataString);
        setData({ data: result, isLoading: false })
      });
  }, []);

  return [isLoading, data];
}

function getNext4MonthsEvents(){
    var date = new Date();
    date.setMonth(date.getMonth() + 4);
    return date.toISOString();
  }
